import crcmod

port_list: list  # 可用串口列表

ser = None  # 串口连接

message = ''  # 串口收到的数据

isSetOK = False

start = False

stop = True

crc16_modbus = crcmod.mkCrcFun(0x18005, initCrc=0xFFFF, rev=True, xorOut=0x0000)

def get_com_list():
    global port_list
    port_list = serial.tools.list_ports.comports()
    return port_list


def print_com_list(get_com_list):
    if len(get_com_list()) <= 0:
        print("无串口设备。")
    else:
        print("可用的串口设备如下：")
        for com in get_com_list():
            print(com)
            # print(list(com)[0],'--',list(com)[1]) #分别输出


def refresh_ports():
    global isSetOK, comlistBox
    isSetOK = False
    comlistBox.delete(0, 'end')
    print_com_list(get_com_list)
    for element in port_list:
        comlistBox.insert('end', element.device)


def connect_serial():
    global ser, comlistBox
    isSetOK = False
    port = comlistBox.get(comlistBox.curselection())
    baud_rate = 115200   #inrich 115200  renjue 9600
    print("selected:", port, baud_rate)
    try:
        ser = serial.Serial(port, baud_rate, timeout=0.5)
    except Exception as e:
        print("exception", e)

    btnSet['state'] = tk.DISABLED
    btnclose['state'] = tk.NORMAL


def close_serial():
    global ser
    if ser:
        ser.close()
        btnclose['state'] = tk.DISABLED
        btnSet['state'] = tk.NORMAL


def send_serail_data(data):
    global ser
    try:
        ser.write(data)
    except Exception as e:
        print("exception", e)


def set_ids():
    cmd = 'AT+SID=' + eID.get() + '\r\n'
    send_serail_data(cmd.encode('ascii'))


def query_ids():
    send_serail_data('AT+SID?\r\n'.encode('ascii'))


def receive_serial_data():
    global ser
    global isSetOK
    global start
    while True:
        #if not start:
            # print("wait fo start set")
        #   continue
        time.sleep(0.01)
        #print("loop")
        if ser != None:
            try:
                data = ser.read(100)
                recvLen = len(data)
                #print(data, len(data), type(data))
                if (0 == recvLen):
                    continue
                #if data[0] == 0xFF:
                #    continue
                str = ""
                try:
                    str = data.decode('ascii')
                except Exception as e:
                    print("exception:", e, data)

                print("str:", str)

            except Exception as e:
                print("exception", e)
                ser = None


def start_broad_setid():
    global start
    global stop
    global isSetOK
    global ser

    if not ser:
        tk.messagebox.showerror(title='error', message='请先连接串口')
        return

    start = True
    stop = False
    isSetOK = False

    btnStartSetID['state'] = tk.DISABLED
    btnStopSetID['state'] = tk.NORMAL


def stop_broad_setid():
    global start
    global stop

    start = False
    stop = True
    btnStartSetID['state'] = tk.NORMAL
    btnStopSetID['state'] = tk.DISABLED

# 设置模组ID
def set_EID(ids: bytes):
    global ser
    '''
    data = bytearray([0x24, 0x50, 0x41, 0x52, 0x41, 0x00, 0x09, 0x10, 0xA9, 0x2E, 0x12, 0x34, 0x56, 0x78, 0x81, 0xB0])
    crc_value = crc16_modbus(data[0:-2])
    crc = crc_value.to_bytes(2, byteorder='big')
    print(len(crc),  crc.hex())
    print(f"CRC-16 Modbus: {crc_value:04X}")  # 输出 16 进制格式

    cmd = data[0:-2] + crc
    print(cmd.hex())
    '''
    head = bytes([0x24, 0x50, 0x41, 0x52, 0x41])
    type = bytes([0x10])  #cmd type

    cmdlen = 1 + len(ids) + 2


    cmddata = head + cmdlen.to_bytes(2, byteorder='big') + type + ids
    crc_value = crc16_modbus(cmddata)
    cmddata = cmddata + crc_value.to_bytes(2, byteorder='big')

    print("cmd binary", cmddata.hex())


    if not ser:
        tk.messagebox.showerror(title='error', message='请先连接串口')
        return
    send_serail_data(cmddata)

def send_single_load_data(ids: bytes):
    global ser
    data = bytearray.fromhex('EF CD 08 20 03 39 20 0C 00 14 AE 68 40 40 01 9A 99 99 3F FC B4')
    packet = ids + data[6:-2]
    #packet = data[6:-2]
    crc_value = crc16_modbus(packet)
    packet = packet + crc_value.to_bytes(2, byteorder='big')
    print(f"CRC-16 Modbus: {crc_value:04X}", ids)
    if not ser:
        tk.messagebox.showerror(title='error', message='请先连接串口')
    # 循环发送  间隔
    send_serail_data(packet)
    print(packet)
    time.sleep(1)

