def modify_channel():
    global ser
    data = bytearray.fromhex('24 50 41 52 41 00 05 11 01 0F CE 64')
    if not ser:
        tk.messagebox.showerror(title='error', message='请先连接串口')
        return

    send_serail_data(data)

def reboot_module():
    global ser
    data = bytearray.fromhex('24 50 41 52 41 00 03 0C F1 B1')
    if not ser:
        tk.messagebox.showerror(title='error', message='请先连接串口')
        return
    print("reboot")
    send_serail_data(data)

def save_configure():
    global ser
    data = bytearray.fromhex('24 50 41 52 41 00 03 1A 3F 30')
    if not ser:
        tk.messagebox.showerror(title='error', message='请先连接串口')
        return
    print("save configure")
    send_serail_data(data)

def query_status():
    global ser
    data = bytearray.fromhex('24 50 41 52 41 00 03 09 F2 71')
    if not ser:
        tk.messagebox.showerror(title='error', message='请先连接串口')
        return
    print("status")
    send_serail_data(data)


def lora_load_test():
    startid = 0xFFFF;

    for i in range(1000):
        ids = startid.to_bytes(6, byteorder='big')
        print(ids)

        set_EID(ids)
        time.sleep(1)
        save_configure()
        time.sleep(1)
        reboot_module()

        time.sleep(10)

        query_status()
        time.sleep(1)
        #send_load_data(ids)
        send_single_load_data(ids)
        startid = startid + 1
        time.sleep(5)


